new_page = open("index.html", "w")
new_page.write('<!DOCTYPE html> <html lang="es"> <head> <meta charset="UTF-8"> <link rel="stylesheet" type="text/css" href="estilos.css"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <!-- production version, optimized for size and speed --> <script src="vue.js"></script> <title>Index</title> </head> <body> <div class="container-fluid"> <div id="app"> {{ message }} </div><br> <div id="app2"> <span v-bind:title="message"> Hover your mouse over me for a few seconds to see my dynamically bound title </span> </div><br> <div id="app3"> <span v-if="seen"> Now you see me </span> </div><br> <div id="app4"> <ul> <li v-for="todo in toditos"> {{ todo.text }} </li> </ul> </div><br> <div id="app5"> <p> {{ message5 }} </p> <button class="btn btn-outline-primary" v-on:mouseover="reverseMessage" v-on:click="reverseMessage"> Reverse message </button> </div><br> <div id="app6"> <div class="form-group row"> <div class="col-sm-4"></div> <div class="col-sm-4"> <p> {{ message6 }} </p> </div> <div class="col-sm-4"></div> </div> <div class="form-group row"> <div class="col-sm-4"></div> <div class="col-sm-4"> <!-- "v-model" para HACER BINDING DE DOBLE VÍA AL INPUT Y A EL PÁRRAFO --> <input class="form-control" v-model="message6"> </div> <div class="col-sm-4"></div> </div> </div> <div id="app-7" class="row"> <div class="col-sm-4"></div> <div class="col-sm-4"> <ol> <!-- Ahora le proporcionamos a cada "cajitaLoca" lo que el objeto "cositas" está representando, para que así su contenido sea dinámico. También necesitamos proporcionarle a cada componente una "llave", que sé explicará luego. --> <todo-item v-for="item in groceryList" v-bind:todo="item" v-bind:key="item.id" ></todo-item> </ol> </div> <div class="col-sm-4"></div> </div> </div> </div> <script src="index.js"></script> </body> </html>')
new_page.close()

new_page2 = open("estilos.css", "w")
new_page2.write('html { scroll-behavior: smooth; } body { font-size: 24px; font-family: Century Gothic; color: #F1F1F1; background-color: #212121; text-align: center; margin: 20px; } h1 { color: #B1B1FF; } .form-control { background-color: #414141; color: #D1D1E1; }')
new_page2.close()